$(document).ready((function(){$("#title").on("input",(function(){var t=$(this).val();$.ajax({url:"https://www.googleapis.com/books/v1/volumes",data:{q:t,maxResults:40},success:function(o){!function(t,o){var e=$("#suggestions");e.empty(),t.filter((function(t){return t.title.toLowerCase().startsWith(o.toLowerCase())})).forEach((function(t){var o=$('<div class="suggestion bg-gray-100 border border-gray-300 py-2 px-4 cursor-pointer"></div>');o.text(t.title),o.on("click",(function(){$("#title").val(t.title),$("#author").val(t.author),$("#pages").val(t.pages),e.empty()})),e.append(o)}))}(o.items.map((function(t){return{title:t.volumeInfo.title,author:t.volumeInfo.authors?t.volumeInfo.authors[0]:"",pages:t.volumeInfo.pageCount?t.volumeInfo.pageCount:""}})),t)}})}))}));